import React from "react";
import { Text, View, StyleSheet, Image, FlatList } from "react-native";
import Options from "./options/options";

const dataOptions = {
    id: Math.random(),
    icon: 'ajuda.png',
    text: 'Área Pix',
}

export default function MenuDisplay(){


    return(
        <View style={styles.container}>
            <View style={styles.account}>
                <View>
                    <Text style={styles.text}>Conta</Text>
                    <Text style={styles.text}>R$ 1,356,98</Text>
                </View>
                    <Image style={styles.iconNext} source={require('../../assets/next.png')}></Image>
            </View>
            <View>
                <FlatList keyExtractor={(item) => item.id} data={dataOptions} renderItem={<Options></Options>}></FlatList>
                <Options></Options>
            </View>
        </View>
    )

}

const styles = StyleSheet.create({
    container: {
        marginTop: 20,
        marginLeft: 20,
    },
    account:{
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    text:{
        fontWeight: 'bold',
        fontSize: 20,
        marginBottom: 4 
    },
    iconNext:{
        width: 20,
        height: 20,
        marginRight: 15
    }
})
