import React from "react";
import { Image, TouchableOpacity } from "react-native";
import { StyleSheet, Text, View } from "react-native";

export default function MoreComponent(text, title){
    return(
        <View style={styles.container}>
            <TouchableOpacity>
                <Image style={styles.image} source={require('../../assets/foto1.png')}></Image>
                <View style={styles.textArea}>
                    <Text style={{width: 192, fontWeight: 'bold', marginBottom: 10}}>Parcele compras no app</Text>
                    <Text numberOfLines={2} style={{width: 192, color: '#696A6E', fontWeight: "400"}}>Descontos em compras à vista no crédito, controle total sobre seu dinheiro</Text>
                </View>
                    <Text style={styles.btn}>Conhecer</Text>
            </TouchableOpacity>
        </View>
    )
}

const styles = StyleSheet.create({    
    container:{
        width: 237,        
        backgroundColor: '#F0F1F5',
        borderRadius:15,
    },
    image:{
        width: 237,
        height: 90,
        borderTopLeftRadius: 15,
        borderTopRightRadius: 15,
    }, 
    textArea:{
        paddingTop: 15,
        paddingLeft: 15,
        paddingBottom: 15,
        marginBottom: 5
    },
    btn:{
        backgroundColor: '#800AD0', 
        color: '#F0F1F5', 
        width:80, 
        borderRadius: 15,
        fontSize: 12, 
        padding: 10,
        marginLeft: 15,
        marginBottom: 15

    }
   
})
