import React from "react";
import { Image, TouchableOpacity } from "react-native";
import { StyleSheet, Text, View } from "react-native";

export default function MoreComponent(text, title){
    return(
        <View style={styles.container}>
            <TouchableOpacity style={styles.btn}>
                <Image style={styles.image} source={require('../../assets/foto1.png')}></Image>
                <View style={styles.textArea}>
                    <Text style={{fontWeight: 'bold', }}>Parcele compras no app</Text>
                    <Text style={{width: 192, color: '#696A6E'}}>Descontos em compras à vista no crédito, controle total sobre seu dinheiro</Text>
                </View>
            </TouchableOpacity>
        </View>
    )
}

const styles = StyleSheet.create({    
    container:{
        width: 237
    },
    image:{
        width: 237,
        height: 90,
        borderTopLeftRadius: 15,
        borderTopRightRadius: 15,
    }, 
    textArea:{
        backgroundColor: '#F0F1F5',
        borderBottomLeftRadius: 15,
        borderBottomRightRadius: 15, 
        paddingTop: 15,
        paddingLeft: 15
    },
   
})
