import React from "react";

import { Image, StyleSheet, Text, TouchableOpacity, View } from "react-native";

export default function CreditCard(){
    return(
        <View style={styles.container}>            
            <View style={styles.row}>
                <Text style={{fontWeight: 'bold', fontSize: 18}}>Cartão de crédito</Text>
                <Image style={styles.icon} source={require("../../assets/next.png")}></Image>
            </View>
            <Text>Fatura atual</Text>
            <Text>R$ 250,00</Text>
            <Text>Limite disponível de R$ 2.678,00</Text>
            <TouchableOpacity>
                <Text>Parcelar compras</Text>
            </TouchableOpacity>
        </View>
    )
}

const styles = StyleSheet.create({
    container:{
        marginTop: 15,
        marginLeft: 20
    },
    icon:{
        width: 20,
        height: 20,
        marginRight: 15
    }, 
    row:{
        flexDirection: 'row', 
        justifyContent: 'space-between',       
    }
})
