import React from "react";
import { Text, View, StyleSheet, Image, FlatList, SafeAreaView } from "react-native";
import OptionComponent from "./options/optionComponent";

const dataOptions = [
    {
        id: Math.random(),
        image: require('../../assets/ajuda.png'),
        text: 'Área Pix',
    },
    {
        id: Math.random(),
        image: require('../../assets/ajuda.png'),
        text: 'Área Pix',
    },
    {
        id: Math.random(),
        image: require('../../assets/ajuda.png'),
        text: 'Área Pix',
    },
    {
        id: Math.random(),
        image: require('../../assets/ajuda.png'),
        text: 'Área Pix',
    }
]

export default function MenuDisplay(){

    return(
        <View style={styles.container}>
            <View style={styles.account}>
                <View>
                    <Text style={styles.text}>Conta</Text>
                    <Text style={styles.text}>R$ 1,356,98</Text>
                </View>
                    <Image style={styles.imageNext} source={require('../../assets/next.png')}></Image>
            </View>
            <SafeAreaView>
                <FlatList 
                    horizontal
                    keyExtractor={(item) => item.id} 
                    data={dataOptions} 
                    renderItem={(item) => <OptionsComponent image={item.item.image} name={item.item.text} {...item}/>}></FlatList>
            </SafeAreaView>
        </View>
    )

}

const styles = StyleSheet.create({
    container: {
        marginTop: 20,
        marginLeft: 20,
    },
    account:{
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    text:{
        fontWeight: 'bold',
        fontSize: 20,
        marginBottom: 4 
    },
    imageNext:{
        width: 20,
        height: 20,
        marginRight: 15
    }, 
    flatList:{
        width: "100%"
    }
})
